use near_sdk::borsh::{self, BorshDeserialize, BorshSerialize}; use near_sdk::{env, near_bindgen}; #[near_bindgen] #[derive(BorshDeserialize, BorshSerialize, Default)] pub struct Counter { count: u64, } #[near_bindgen] impl Counter { pub fn get_count(&self) -> u64 { self.count } pub fn increment(&mut self) { self.count += 1; let log_message = format!("Count incremented to {}", self.count); env::log_str(&log_message); } }
